import React, { useEffect, useState } from "react";
import {
  categoryNotificationAction,
  createNotificationCategoryAction,
} from "layouts/notification/notificationAction";
import { useDispatch } from "react-redux";
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDInput from "components/MDInput";
import MDButton from "components/MDButton";
import { getAllPatient } from "_api/patientApi";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function WhatsappForm() {
  const [inputTitle, setInputTitle] = useState();
  const [inputDescription, setInputDescription] = useState();
  const [patientData, setPatientData] = useState();
  const [patientOptions, setPatientOptions] = useState([]);
  const MySwal = withReactContent(Swal);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(categoryNotificationAction());
  }, []);

  useEffect(() => {
    if (!patientData) {
      getAllPatient()
        .then((response) => {
          setPatientData(response.data.data);
        })
        .catch((error) => {
          MySwal.fire({
            title: "Error Get Patient Data",
            icon: "error",
          });
        });
    } else {
      let tempOptions = [];
      patientData.map((item, index) => {
        tempOptions = [...tempOptions, { value: item.user_id, label: item.user.name }];
      });
      setPatientOptions(tempOptions);
    }
  }, [patientData]);

  function handleSubmit() {
    dispatch(createNotificationCategoryAction(inputTitle, inputDescription));
  }
  return (
    <div className="animation-popup flex flex-col gap-[20px] mt-[20px]">
      <MDBox display="flex" justifyContent="center" alignItems="center" gap="20px">
        <MDTypography variant="h6" color="text">
          CREATE CATEGORY NOTIFICATION
        </MDTypography>
      </MDBox>
      <MDBox display="flex-column" alignItems="center" gap="20px">
        <MDTypography variant="h6" color="text">
          Select Patient
        </MDTypography>
        <Select
          className="basic-single text-[14px]"
          closeMenuOnSelect={false}
          components={animatedComponents}
          onChange={(e) => handleSelectedPatient(e)}
          isMulti
          options={patientOptions}
        />
      </MDBox>
      <MDBox display="flex-column" alignItems="center" gap="20px">
        <MDTypography variant="h6" color="text">
          Input Title Category
        </MDTypography>
        <MDInput
          fullWidth
          required
          label="Message"
          onChange={(e) => setInputTitle(e.target.value)}
          multiline
          rows={1}
        />
      </MDBox>
      <MDBox display="flex-column" alignItems="center" gap="20px">
        <MDTypography variant="h6" color="text">
          Input Description Category
        </MDTypography>
        <MDInput
          fullWidth
          required
          label="Message"
          onChange={(e) => setInputDescription(e.target.value)}
          multiline
          rows={5}
        />
      </MDBox>

      <MDButton variant="gradient" color="success" onClick={() => handleSubmit()}>
        Send
      </MDButton>
    </div>
  );
}
