import React from "react";
import MDButton from "components/MDButton";

import MDInput from "components/MDInput";
import MDTypography from "components/MDTypography";
import { useState } from "react";
import DynamicForm from "./DynamicForm";

export default function EditMedicalRecordForm({ medicalRecord }) {
  const [fieldMedicalRecord, setFieldMedicalRecord] = useState(medicalRecord);

  function addFieldHandle() {
    setFieldMedicalRecord([...fieldMedicalRecord, { key: " ", value: " " }]);
  }

  function removeFieldHandle(index) {
    console.log(fieldMedicalRecord);
    let tempData = fieldMedicalRecord;
    tempData.splice(index);
    console.log(tempData);
    setFieldMedicalRecord(tempData);
    console.log(fieldMedicalRecord);
  }

  console.log(fieldMedicalRecord);

  return (
    <>
      <div className="w-full flex flex-col justify-center gap-[20px] px-[20px] border-blue-500 border-solid border-l-4">
        <DynamicForm
          removeHandle={(index) => removeFieldHandle(index)}
          fieldList={fieldMedicalRecord}
        />
        {/* {fieldMedicalRecord.map((item, index) => {
          return (
            <div key={index} className="w-full flex flex-col gap-[5px] py-[20px]">
            <MDTypography color="dark" fontSize="14px" fontWeight="bold">
            {item.key}
            </MDTypography>
            <div className="w-full bg-gray-100 p-2 rounded-xl">
            <MDTypography color="dark" fontSize="14px" fontWeight="regular">
            {item.value}
            </MDTypography>
            </div>
            </div>
            );
          })} */}
      </div>
      <div className="w-full flex gap-[10px]">
        <MDButton variant="gradient" type="submit" color="warning">
          Edit
        </MDButton>
      </div>
      <div className="w-full flex gap-[10px]">
        <MDButton
          variant="gradient"
          onClick={() => {
            addFieldHandle();
          }}
          color="info"
        >
          Add Record
        </MDButton>
      </div>
    </>
  );
}
