import React, { useEffect } from "react";
import MDInput from "components/MDInput";
import { useForm } from "react-hook-form";
import MDButton from "components/MDButton";

export default function DynamicForm({ fieldList, removeHandle, addHandle }) {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    if (window.confirm("Yakin untuk Submit?") == true) {
      console.log(data);
    }
  };

  console.log(String(fieldList.length));
  return (
    <>
      <form className="mb-[20px]" onSubmit={handleSubmit(onSubmit)}>
        {fieldList.map((item, index) => {
          return (
            <div key={index} className="w-full flex flex-col gap-[5px] py-[20px]">
              <div className="w-full bg-gray-100 p-2 rounded-xl flex flex-col gap-[10px] relative">
                <div className="flex gap-[10px]">
                  <MDInput
                    fullWidth
                    required
                    label="Key"
                    defaultValue={item.key}
                    {...register(item.key)}
                    // onChange={(e) => {
                    //   setRPS(e.target.value);
                    // }}
                    multiline
                    rows={1}
                  />
                  <MDButton
                    variant="gradient"
                    onClick={() => {
                      removeHandle(index);
                    }}
                    color="primary"
                  >
                    Hapus
                  </MDButton>
                </div>

                <MDInput
                  fullWidth
                  required
                  label="Value"
                  defaultValue={item.value}
                  {...register(item.key + "value")}
                  // onChange={(e) => {
                  //   setRPS(e.target.value);
                  // }}
                  multiline
                  rows={5}
                />
              </div>
            </div>
          );
        })}
        <div className="w-full flex gap-[10px] text-right justify-end ">
          <MDButton
            variant="gradient"
            onClick={() => {
              addHandle(fieldList.length);
            }}
            color="info"
          >
            Add Record
          </MDButton>
          <MDButton variant="gradient" type="submit" color="success">
            Save
          </MDButton>
        </div>
      </form>
    </>
  );
}
