import React, { useEffect, useState } from "react";

import MDBox from "components/MDBox";
import BackButton from "components/extend/Button/BackButton";
import MDTypography from "components/MDTypography";
import MDInput from "components/MDInput";
import { Link } from "react-router-dom";
import MDButton from "components/MDButton";
import { getAllPatient } from "_api/patientApi";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
import Select from "react-select";
import makeAnimated from "react-select/animated";

export default function CreateNotification() {
  const [patientData, setPatientData] = useState();
  const [patientOptions, setPatientOptions] = useState([]);
  const animatedComponents = makeAnimated();
  const MySwal = withReactContent(Swal);
  useEffect(() => {
    if (!patientData) {
      getAllPatient()
        .then((response) => {
          setPatientData(response.data.data);
        })
        .catch((error) => {
          MySwal.fire({
            title: "Error Get Patient Data",
            icon: "error",
          });
        });
    } else {
      let tempOptions;
      console.log(patientData);
      patientData.map((item, index) => {
        tempOptions = [...patientOptions, { value: item.user_id, label: item.user.name }];
      });
      console.log(tempOptions);
      setPatientOptions(tempOptions);
    }
  }, [patientData]);
  return (
    <>
      <MDBox
        mx={2}
        mt={-3}
        py={3}
        px={2}
        variant="gradient"
        bgColor="info"
        borderRadius="lg"
        coloredShadow="info"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
      >
        <MDBox display="flex" alignItems="center" gap="20px">
          <BackButton to="/notification/list-notification" />
          <MDTypography variant="h6" color="white">
            Create Notification
          </MDTypography>
        </MDBox>
      </MDBox>
      <div className="w-full flex flex-col justify-center items-center gap-[20px] px-[20px] my-[40px]">
        <div className="w-2/3  p-2 rounded-xl flex flex-col gap-[10px] relative">
          <MDInput
            id="appointment"
            select
            label="Select Appointment"
            // onChange={(e) => setSelectedAppointment(e.target.value)}
            SelectProps={{
              native: true,
            }}
            fullWidth
            helperText="Please Select the Appointment first..."
          >
            <option value="" selected></option>
            <option value="all">-----ALL PATIENT-----</option>
            {patientData?.map((item, index) => (
              <option value={index}>{`${item.name} || ${item.user.phone_number}`}</option>
            ))}
          </MDInput>
          <Select
            closeMenuOnSelect={false}
            components={animatedComponents}
            isMulti
            options={patientOptions}
          />
        </div>
      </div>
    </>
  );
}
