import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import MDTypography from "components/MDTypography";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector, shallowEqual } from "react-redux";
import * as actions from "../../medicalRecordAction";
import { dateFormater } from "helpers/DateHelpers";
import MDButton from "components/MDButton";
import { useForm } from "react-hook-form";
import MDInput from "components/MDInput";

export default function DetailMedicalRecord() {
  // Get Params
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const params = useParams();
  const dispatch = useDispatch();
  const { medicalRecord } = useSelector(
    (state) => ({
      medicalRecord: state.medicalRecord.medicalRecord,
    }),
    shallowEqual
  );
  const [fieldMedicalRecord, setFieldMedicalRecord] = useState([]);

  function addField() {
    console.log(fieldMedicalRecord);
    setFieldMedicalRecord([...fieldMedicalRecord, { "": "" }]);
  }

  useEffect(() => {
    dispatch(actions.detailMedicalRecord(params.id));

    return () => {
      dispatch(actions.detailMedicalRecord(undefined));
    };
  }, [params.id]);

  useEffect(() => {
    setFieldMedicalRecord(medicalRecord?.records);
  }, [medicalRecord?.records]);

  console.log(medicalRecord);

  const onSubmit = (data) => console.log(data);

  return (
    <div className="flex flex-col">
      <div className="flex">
        <div className="w-1/2 p-[20px]">
          <MDTypography fontSize="20px" fontWeight="bold">
            Appointment Detail
          </MDTypography>

          <div className=" flex gap-[10px] ">
            <MDTypography width="40%" color="text" fontSize="14px">
              Appointment Date
            </MDTypography>
            <MDTypography width="10%" color="text" fontSize="14px">
              :
            </MDTypography>
            <MDTypography width="50%" color="text" fontSize="14px">
              {dateFormater(medicalRecord?.date)}
            </MDTypography>
          </div>
          <div className=" flex gap-[10px] ">
            <MDTypography width="40%" color="text" fontSize="14px">
              Appointment Hours
            </MDTypography>
            <MDTypography width="10%" color="text" fontSize="14px">
              :
            </MDTypography>
            <MDTypography width="50%" color="text" fontSize="14px">
              {`${medicalRecord?.time} WIB`}
            </MDTypography>
          </div>
          <div className=" flex gap-[10px] ">
            <MDTypography width="40%" color="text" fontSize="14px">
              Physiotherapy
            </MDTypography>
            <MDTypography width="10%" color="text" fontSize="14px">
              :
            </MDTypography>
            <MDTypography width="50%" color="text" fontSize="14px">
              {`${medicalRecord?.therapist_detail.name}`}
            </MDTypography>
          </div>
        </div>
        <div className="w-1/2 p-[20px]">
          <MDTypography fontSize="20px" fontWeight="bold">
            Patient Detail
          </MDTypography>

          <div className=" flex gap-[10px] ">
            <MDTypography width="40%" color="text" fontSize="14px">
              Name
            </MDTypography>
            <MDTypography width="10%" color="text" fontSize="14px">
              :
            </MDTypography>
            <MDTypography width="50%" color="text" fontSize="14px">
              {medicalRecord?.patient_detail.name}
            </MDTypography>
          </div>
          <div className=" flex gap-[10px] ">
            <MDTypography width="40%" color="text" fontSize="14px">
              Phone Number
            </MDTypography>
            <MDTypography width="10%" color="text" fontSize="14px">
              :
            </MDTypography>
            <MDTypography width="50%" color="text" fontSize="14px">
              {medicalRecord?.patient_detail.phone_number}
            </MDTypography>
          </div>
          <div className=" flex gap-[10px] ">
            <MDTypography width="40%" color="text" fontSize="14px">
              Email
            </MDTypography>
            <MDTypography width="10%" color="text" fontSize="14px">
              :
            </MDTypography>
            <MDTypography width="50%" color="text" fontSize="14px">
              {medicalRecord?.patient_detail.email}
            </MDTypography>
          </div>
        </div>
      </div>
      <div className="w-full p-[20px] flex flex-col justify-center">
        <MDTypography color="dark" fontSize="20px" fontWeight="bold">
          Medical Record
        </MDTypography>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="w-full flex flex-col justify-center gap-[20px] px-[20px] border-blue-500 border-solid border-l-4">
            {fieldMedicalRecord && fieldMedicalRecord.length > 0
              ? fieldMedicalRecord.map((item) => {
                  return (
                    <div className="w-full flex flex-col gap-[5px] py-[20px]">
                      <div className="w-full bg-gray-100 p-2 rounded-xl flex flex-col gap-[10px]">
                        <MDInput
                          fullWidth
                          required
                          label="Key"
                          disabled={true}
                          defaultValue={item.key}
                          {...register(item.key)}
                          // onChange={(e) => {
                          //   setRPS(e.target.value);
                          // }}
                          multiline
                          rows={1}
                        />
                        <MDInput
                          fullWidth
                          required
                          label="Value"
                          disabled={true}
                          defaultValue={item.value}
                          {...register(item.key + "value")}
                          // onChange={(e) => {
                          //   setRPS(e.target.value);
                          // }}
                          multiline
                          rows={5}
                        />
                      </div>
                    </div>
                  );
                })
              : null}
            {fieldMedicalRecord && fieldMedicalRecord.length > 0
              ? fieldMedicalRecord.map((item, index) => {
                  return (
                    <div key={index} className="w-full flex flex-col gap-[5px] py-[20px]">
                      <MDTypography color="dark" fontSize="14px" fontWeight="bold">
                        {item.key}
                      </MDTypography>
                      <div className="w-full bg-gray-100 p-2 rounded-xl">
                        <MDTypography color="dark" fontSize="14px" fontWeight="regular">
                          {item.value}
                        </MDTypography>
                      </div>
                    </div>
                  );
                })
              : null}
          </div>
          <div className="w-full flex gap-[10px]">
            <MDButton variant="gradient" type="submit" color="warning">
              Edit
            </MDButton>
          </div>
          <div className="w-full flex gap-[10px]">
            <MDButton
              variant="gradient"
              onClick={() => {
                addField();
              }}
              color="info"
            >
              Add Record
            </MDButton>
          </div>
        </form>
      </div>

      <div className="w-full p-[20px] flex flex-col justify-center">
        <MDTypography color="dark" fontSize="20px" fontWeight="bold">
          Action
        </MDTypography>
        <div className="w-full flex gap-[10px]">
          <MDButton variant="gradient" color="warning">
            Edit
          </MDButton>
        </div>
      </div>
    </div>
  );
}

DetailMedicalRecord.defaultProps = {
  appointment_date: "2022-08-11T00:00:00.000Z",
  appointment_time: "19.00",
  physiotherapy: "test",
};

DetailMedicalRecord.propTypes = {
  appointment_date: PropTypes.string,
  appointment_time: PropTypes.string,
  physiotherapy: PropTypes.string,
};
