import React from "react";
import MDInput from "components/MDInput";
import { useForm } from "react-hook-form";

export default function DynamicForm({ fieldList }) {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);
  return (
    <>
      <form onSubmit={handleSubmit(onSubmit)}>
        {fieldList.map((item, index) => {
          return (
            <div key={index} className="w-full flex flex-col gap-[5px] py-[20px]">
              <div className="w-full bg-gray-100 p-2 rounded-xl flex flex-col gap-[10px] relative">
                <button className="bg-danger px-[20px]">-</button>
                <MDInput
                  fullWidth
                  required
                  label="Key"
                  defaultValue={item.key}
                  {...register(item.key)}
                  // onChange={(e) => {
                  //   setRPS(e.target.value);
                  // }}
                  multiline
                  rows={1}
                />
                <MDInput
                  fullWidth
                  required
                  label="Value"
                  defaultValue={item.value}
                  {...register(item.key + "value")}
                  // onChange={(e) => {
                  //   setRPS(e.target.value);
                  // }}
                  multiline
                  rows={5}
                />
              </div>
            </div>
          );
        })}
      </form>
    </>
  );
}
