import React, { useEffect } from "react";
import MDInput from "components/MDInput";
import { useForm } from "react-hook-form";
import MDButton from "components/MDButton";

export default function DynamicForm({ fieldList, setFieldMedicalRecord }) {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    console.log("masuk");
  }, [fieldList]);

  function removeFieldHandle(index) {
    let tempField = fieldMedicalRecord;
    tempField.splice(index, 1);

    setFieldMedicalRecord(tempField);

    console.log(fieldMedicalRecord);
  }

  const onSubmit = (data) => console.log(data);
  return (
    <>
      <form onSubmit={handleSubmit(onSubmit)}>
        {fieldList.map((item, index) => {
          return (
            <div key={index} className="w-full flex flex-col gap-[5px] py-[20px]">
              <div className="w-full bg-gray-100 p-2 rounded-xl flex flex-col gap-[10px] relative">
                <div className="flex gap-[10px]">
                  <MDInput
                    fullWidth
                    required
                    label="Key"
                    defaultValue={item.key}
                    {...register(item.key)}
                    // onChange={(e) => {
                    //   setRPS(e.target.value);
                    // }}
                    multiline
                    rows={1}
                  />
                  <MDButton
                    variant="gradient"
                    onClick={() => {
                      removeFieldHandle(index);
                    }}
                    color="primary"
                  >
                    Hapus
                  </MDButton>
                </div>

                <MDInput
                  fullWidth
                  required
                  label="Value"
                  defaultValue={item.value}
                  {...register(item.key + "value")}
                  // onChange={(e) => {
                  //   setRPS(e.target.value);
                  // }}
                  multiline
                  rows={5}
                />
              </div>
            </div>
          );
        })}
      </form>
    </>
  );
}
